package Week1day2.Assignments;

public class CheckPossNeg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number =35;
		if(number>0)
		{
			System.out.println(number +" The number is possitive");
		}
		else if(number<0)
		{
			System.out.println(number +" The number is negative");
		}
		else 
		{
			System.out.println(number +" The number is neither positive nor negative");
		}
	}

}
